  title: string
  items?: MenuItem[]
}

interface CardSidenavProps {
  items: MenuItem[]
}

export function FolderTreeMenu({ items }: CardSidenavProps) {
  const { openStates, toggleSubmenu } = useSubmenuState()

  const renderMenuItem = (item: MenuItem) => {
